mapper.select:
SELECT Fact.ID AS pkidFact, SourceLink.ID AS pkidSourceLink, SourceLink.Stars AS stars, SourceLink.Justification AS just, Source.ID AS pkidCitation, Source.PageNumber AS page, Source.Comment AS comment, Source.Footnote AS footnote, Source.PID AS apid, MasterSource.Author AS author, MasterSource.Title AS title, MasterSource.PublisherLocation AS placePub, MasterSource.PublisherName AS pub, MasterSource.PublishDate AS datePub, MasterSource.CallNumber AS callno, MasterSource.Comments AS source, MasterSource.PID AS apidSource, MediaFile.ID AS media_pkidMedia, MediaFile.FileName AS media_file, (SELECT Url FROM WebLink WHERE WebLink.LinkID = Source.ID AND WebLink.LinkTableID = 16 LIMIT 1) AS weblink FROM Fact LEFT OUTER JOIN SourceLink ON (SourceLink.LinkTableID = 2 AND SourceLink.LinkID = Fact.ID) LEFT OUTER JOIN Source ON Source.ID = SourceLink.SourceID LEFT OUTER JOIN MasterSource ON MasterSource.ID = Source.MasterSourceID LEFT OUTER JOIN MediaLink ON MediaLink.LinkTableID = 16 AND MediaLink.LinkID = Source.ID LEFT OUTER JOIN MediaFile ON MediaFile.ID = MediaLink.MediaFileID WHERE Fact.LinkTableID = #{table.id} AND Fact.LinkID = #{id}
UNION ALL
SELECT Fact.ID AS pkidFact, SourceLink.ID AS pkidSourceLink, SourceLink.Stars AS stars, SourceLink.Justification AS just, Source.ID AS pkidCitation, Source.PageNumber AS page, Source.Comment AS comment, Source.Footnote AS footnote, Source.PID AS apid, MasterSource.Author AS author, MasterSource.Title AS title, MasterSource.PublisherLocation AS placePub, MasterSource.PublisherName AS pub, MasterSource.PublishDate AS datePub, MasterSource.CallNumber AS callno, MasterSource.Comments AS source, MasterSource.PID AS apidSource, MediaFile.ID AS media_pkidMedia, MediaFile.FileName AS media_file, (SELECT Url FROM WebLink WHERE WebLink.LinkID = Source.ID AND WebLink.LinkTableID = 16 LIMIT 1) AS weblink FROM Fact LEFT OUTER JOIN SourceLink ON (SourceLink.LinkTableID = 2 AND SourceLink.LinkID = Fact.ID) LEFT OUTER JOIN Source ON Source.ID = SourceLink.SourceID LEFT OUTER JOIN MasterSource ON MasterSource.ID = Source.MasterSourceID LEFT OUTER JOIN MediaLink ON MediaLink.LinkTableID = 10 AND MediaLink.LinkID = MasterSource.ID LEFT OUTER JOIN MediaFile ON MediaFile.ID = MediaLink.MediaFileID WHERE Fact.LinkTableID = #{table.id} AND Fact.LinkID = #{id}
UNION ALL
SELECT Fact.ID AS pkidFact, 0 AS pkidSourceLink, null AS stars, null AS just, random() AS pkidCitation, null AS page, null AS comment, null AS footnote, null AS apid, null AS author, '[unknown source]' AS title, null AS placePub, null AS pub, null AS datePub, null AS callno, null AS source, null AS apidSource, MediaFile.ID AS media_pkidMedia, MediaFile.FileName AS media_file, null AS weblink FROM Fact LEFT OUTER JOIN MediaLink ON MediaLink.LinkTableID = 2 AND MediaLink.LinkID = Fact.ID LEFT OUTER JOIN MediaFile ON MediaFile.ID = MediaLink.MediaFileID WHERE Fact.LinkTableID = #{table.id} AND Fact.LinkID = #{id} AND MediaFile.ID IS NOT NULL
UNION ALL
SELECT Fact.ID AS pkidFact, 0 AS pkidSourceLink, null AS stars, null AS just, random() AS pkidCitation, null AS page, null AS comment, Note.NoteText AS footnote, null AS apid, null AS author, null AS title, null AS placePub, null AS pub, null AS datePub, null AS callno, null AS source, null AS apidSource, null AS media_pkidMedia, null AS media_file, null AS weblink FROM Fact INNER JOIN Note ON Note.LinkTableID = 2 AND Note.LinkID = Fact.ID WHERE Fact.LinkTableID = #{table.id} AND Fact.LinkID = #{id}
ORDER BY pkidFact
